definition.script:
pipeline {
    agent any

    environment {
        AWS_ACCOUNT_ID = "${env.AWS_ACCOUNT_ID}"
        VPC_ID = "${env.VPC_ID}"
        CODECOMMIT_REPO_URL = 'https://git-codecommit.us-east-1.amazonaws.com/v1/repos/ncompasstv-api'
        DOCKER_IMAGE_NAME = '335883086205.dkr.ecr.us-east-1.amazonaws.com/ncompasstv-api'
        DOCKER_IMAGE_TAG = "${params.PORT}"
        PORT = "${params.PORT}"
        AWS_DEFAULT_REGION = "${env.AWS_DEFAULT_REGION}"
        AWS_ACCESS_KEY_ID = "${env.AWS_ACCESS_KEY_ID}"
        AWS_SECRET_ACCESS_KEY = "${env.AWS_SECRET_ACCESS_KEY}"
        IP_ADDRESS = "${env.IP_ADDRESS}"
        CONTAINER_NAME = "api" + "${params.PORT}"
        LOADBALANCER_ARN = "arn:aws:elasticloadbalancing:us-east-1:335883086205:loadbalancer/app/nc-alb-dev/e42f62ce34c0f13e"
        SSH_CREDENTIALS_ID = "nctv-private-ssh"
        REMOTE_SERVER = "172.10.30.105"
        REMOTE_USER = "ubuntu"
        PEM_FILE_PATH = "/var/lib/jenkins/.ssh/nc-nvir-bastion-dev-key.pem"
        AWS_CREDENTIALS_ID = 'aws-credentials'
    }

    parameters {
        string(name: 'BRANCH_TO_BUILD', defaultValue: 'master', description: 'Enter the branch name to build')
    }

    stages {

         stage('CHECKOUT') {
            steps {
                script {
                    // Use CodeCommit credentials for Git checkout
                    withCredentials([usernamePassword(credentialsId: 'aws-codecommit-credentials', usernameVariable: 'CODECOMMIT_USERNAME', passwordVariable: 'CODECOMMIT_PASSWORD')]) {
                        git credentialsId: 'aws-codecommit-credentials', url: CODECOMMIT_REPO_URL, branch: params.BRANCH_TO_BUILD
                    }
                    //sh "echo Checkout"
                }
            }
        }


        stage('BUILD') {
            steps {
               script {

                    // Assuming your project has a Dockerfile in the root directory
                    dockerImage = docker.build("${DOCKER_IMAGE_NAME}:${DOCKER_IMAGE_TAG}")
                    //sh "echo Build"
                }
            }
        }

        stage('ECR LOGIN') {
            steps {
                script {
                    withCredentials([[
                        $class: 'AmazonWebServicesCredentialsBinding', 
                        credentialsId: AWS_CREDENTIALS_ID,
                        accessKeyVariable: 'AWS_ACCESS_KEY_ID',
                        secretKeyVariable: 'AWS_SECRET_ACCESS_KEY'
                    ]]) {
                        sh 'aws --version'
                        sh "aws ecr get-login-password --region ${AWS_REGION} | docker login --username AWS --password-stdin ${DOCKER_IMAGE_NAME}:${PORT}"
                    }
                }
            }
        }

        stage('PUSH') {
            steps {
                script {
                    dockerImage.push()
                }
            }
        }

        stage('DELETE LISTENER') {
            steps {
                script {
                    // Define Python script
                    def pythonScript = """
import boto3

def delete_listener(load_balancer_arn, port):
    elbv2 = boto3.client("elbv2")

    # Describe listeners to find the ARN of the listener by port
    response = elbv2.describe_listeners(LoadBalancerArn=load_balancer_arn)

    print(response["Listeners"])

    # Search for the listener with the specified port
    for listener in response["Listeners"]:
        print(listener["Port"], "=>", port)
        if listener["Port"] == port:
            listener_arn = listener["ListenerArn"]
            print("Deleting listener with ARN: {listener_arn}")

            # Delete the listener
            elbv2.delete_listener(ListenerArn=listener_arn)
            print("Listener deleted successfully.")
            return

    print("Listener with specified port not found.")

# Replace 'your-load-balancer-arn' with the name of your load balancer
load_balancer_arn = "${LOADBALANCER_ARN}"
# Replace 'your-listener-port' with the port of the listener you want to delete
listener_port = ${PORT}

delete_listener(load_balancer_arn, listener_port)
                    """

                    // Execute Python script
                    sh """
echo '''${pythonScript}''' > delete_listener.py
pip install boto3
python3 delete_listener.py
                    """
                }
            }
        }


        stage('ATTACH TO ALB') {
            steps {
                script {
                    def TG_NAME = "tg-api-${PORT}"

                    sh """
                        aws elbv2 create-target-group \
                            --name ${TG_NAME} \
                            --protocol HTTP \
                            --port ${PORT} \
                            --target-type ip \
                            --vpc-id ${VPC_ID} \
                            --health-check-path /api/values
                    """

                    def CMD_TG_ARN = """
                        aws elbv2 describe-target-groups \
                            --query "TargetGroups[?TargetGroupName=='${TG_NAME}'].TargetGroupArn" \
                            --output text
                    """
                    def TG_ARN = sh(script: CMD_TG_ARN, returnStdout: true).trim()

                    sh """
                        aws elbv2 register-targets \
                            --target-group-arn ${TG_ARN} \
                            --targets Id=${REMOTE_SERVER}
                    """

                    sh """
                        aws elbv2 create-listener \
                            --load-balancer-arn arn:aws:elasticloadbalancing:us-east-1:335883086205:loadbalancer/app/nc-alb-dev/e42f62ce34c0f13e \
                            --protocol HTTPS \
                            --port ${PORT} \
                            --certificates CertificateArn=arn:aws:acm:us-east-1:335883086205:certificate/80bc7f1b-9c1f-4ebd-b70a-1ebe672af3af \
                            --ssl-policy ELBSecurityPolicy-TLS13-1-2-2021-06 \
                            --default-actions Type=forward,TargetGroupArn=${TG_ARN}
                    """
                }
            }
        }

        stage('ECR LOGIN REMOTE') {
            steps {

                script {
                    withCredentials([[
                        $class: 'AmazonWebServicesCredentialsBinding', 
                        credentialsId: AWS_CREDENTIALS_ID,
                        accessKeyVariable: 'AWS_ACCESS_KEY_ID',
                        secretKeyVariable: 'AWS_SECRET_ACCESS_KEY'
                    ]]) {

                        sshCommand remote: [
                            name: 'remote-server',
                            host: REMOTE_SERVER,
                            user: 'ubuntu', // Replace with your actual SSH username
                            identityFile: PEM_FILE_PATH,
                            allowAnyHosts: true
                        ], command: """
                            # Set AWS credentials as environment variables
                            export AWS_ACCESS_KEY_ID=${AWS_ACCESS_KEY_ID}
                            export AWS_SECRET_ACCESS_KEY=${AWS_SECRET_ACCESS_KEY}

                            aws ecr get-login-password --region ${AWS_REGION} | docker login --username AWS --password-stdin ${DOCKER_IMAGE_NAME}:${PORT}
                        """
                    }
                }
            }
        }

        stage('DEPLOY') {
            steps {
                script {
                    try {
                        // Execute a series of commands on the remote server
                        sshCommand remote: [
                            name: 'remote-server',
                            host: REMOTE_SERVER,
                            user: 'ubuntu', // Replace with your actual SSH username
                            identityFile: PEM_FILE_PATH,
                            allowAnyHosts: true
                        ], command: """
                            aws ecr get-login-password --region ${AWS_REGION} | docker login --username AWS --password-stdin ${DOCKER_IMAGE_NAME}:${PORT}
                            docker rm -f ${CONTAINER_NAME}
                            docker run -d -p ${PORT}:80 -eDB_CONNECTION=N-CompassDB-new -eAPI_ENV=dev -eAWS_ACCESS_KEY=${AWS_ACCESS_KEY_ID} -eAWS_ACCESS_SECRET_KEY=${AWS_SECRET_ACCESS_KEY} -eAWS_IMAGE_BUCKET=nctv-images-dev -eAWS_IMAGE_BUCKET_URL=https://nctv-images-dev.s3.amazonaws.com -eAWS_SQS_URL=https://sqs.us-east-1.amazonaws.com/335883086205/nctv-screenshots --name ${CONTAINER_NAME} ${DOCKER_IMAGE_NAME}:${PORT}
                        """
                    } catch (Exception e) {
                        error "Failed to execute commands via SSH: ${e.message}"
                    }
                }
            }
        }

    }

    post {
        success {
            echo 'Build and deployment successful!'
        }
        failure {
            echo 'Build or deployment failed!'
        }
    }
}
