definition.script:
pipeline { agent any
environment { AWS_DEFAULT_REGION = "${env.AWS_DEFAULT_REGION}" AWS_ACCESS_KEY_ID = "${env.AWS_ACCESS_KEY_ID}" AWS_SECRET_ACCESS_KEY = "${env.AWS_SECRET_ACCESS_KEY}" DB_HOST = "nc-nvir-dev-rds-03.coot01xe9yza.us-east-1.rds.amazonaws.com" DB_NAME = "n_compass" AWS_S3_BUCKET = "nc-rds-backup-dev" GZ_FILE_NAME = "backup_${new Date().format('yyyyMMdd_HHmmss')}.sql.gz" }
stages {
stage('MySQL Backup and Gzip') { steps { script { // Create a backup of the MySQL database and gzip it withCredentials([usernamePassword(credentialsId: 'mysql-creds-dev', usernameVariable: 'DB_USER', passwordVariable: 'DB_PASSWORD')]) { sh """ mysqldump -h ${env.DB_HOST} -u ${env.DB_USER} -p'${env.DB_PASSWORD}' ${env.DB_NAME} \ activities \ advertisers \ blacklisted_contents \ categories \ cities \ cities_state_data \ content_spacer_algos \ content_spacer_categories \ content_spacer_licenses \ content_spacer_playlists \ contents \ countries \ dealer_address_books \ dealer_admins \ dealer_contract_details \ dealer_templates \ dealer_unused_contents \ dealer_values \ dealers \ dealers_contract_files \ dealers_territory_files \ dma \ feed_fillers \ feed_global_settings \ feed_news \ feed_slides \ feed_types \ feed_weathers \ feeds \ field_group_fields \ field_group_values \ field_groups \ filler_contents \ filler_group_blacklisted \ filler_groups \ filler_logs \ filler_playlist_contents \ filler_playlist_dealers \ filler_playlist_groups \ filler_playlists \ global_settings \ host_field_groups \ host_files \ host_support_tickets \ hosts \ hosts_import \ installation_checklist_items \ installation_checklists \ license_favorites \ license_installation_checklists \ license_playlist_contents \ license_screens \ licenses \ licenses_acquired \ local_labs \ notification_logs \ notifications \ orders_viewed \ permissions \ placer_dumps \ placers \ player_app_versions \ player_apps \ player_asset_server \ player_ui \ playlist_content_credits \ playlist_contents \ playlist_contents_schedule \ playlists \ purchase_items \ release_notes \ roles \ screen_template_zone_playlists \ screen_types \ screens \ spacer_algorithms \ spacer_categories \ spacer_licenses \ spacers \ states \ stripe_users \ tag_details \ tag_types \ tags \ template_zones \ templates \ time_zones \ tv_brands \ user_roles \ users \ webhooks \ webhooks_convert | gzip > ${env.GZ_FILE_NAME} """ } } } }
stage('Upload to S3') {
steps { // Upload the gzip file to AWS S3 sh "aws s3 cp ${env.GZ_FILE_NAME} s3://${env.AWS_S3_BUCKET}/"
//Delete local file sh "rm ${env.GZ_FILE_NAME}" } }
}
post { success { echo 'Dump successful!' } failure { echo 'Dump failed!' } } }
